@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);

  --color-background: hsl(var(--background));
  --color-foreground: hsl(var(--foreground));
  --color-card: hsl(var(--card));
  --color-card-foreground: hsl(var(--card-foreground));
  --color-popover: hsl(var(--popover));
  --color-popover-foreground: hsl(var(--popover-foreground));
  --color-primary: hsl(var(--primary));
  --color-primary-foreground: hsl(var(--primary-foreground));
  --color-secondary: hsl(var(--secondary));
  --color-secondary-foreground: hsl(var(--secondary-foreground));
  --color-muted: hsl(var(--muted));
  --color-muted-foreground: hsl(var(--muted-foreground));
  --color-accent: hsl(var(--accent));
  --color-accent-foreground: hsl(var(--accent-foreground));
  --color-destructive: hsl(var(--destructive));
  --color-destructive-foreground: hsl(var(--destructive-foreground));
  --color-border: hsl(var(--border));
  --color-input: hsl(var(--input));
  --color-ring: hsl(var(--ring));
  --color-chart-1: hsl(var(--chart-1));
  --color-chart-2: hsl(var(--chart-2));
  --color-chart-3: hsl(var(--chart-3));
  --color-chart-4: hsl(var(--chart-4));
  --color-chart-5: hsl(var(--chart-5));

  --font-sans: 'Outfit', sans-serif;
  --font-serif: 'Lora', 'Amiri', serif;
  --font-arabic: 'Scheherazade New', 'Amiri Quran', 'Amiri', serif;
  --font-bengali: 'Noto Sans Bengali', 'Outfit', sans-serif;
  --font-urdu: 'Noto Nastaliq Urdu', 'Scheherazade New', serif;
  --font-hindi: 'Noto Sans Devanagari', 'Outfit', sans-serif;
  --font-tamil: 'Noto Sans Tamil', 'Outfit', sans-serif;
  --font-malayalam: 'Noto Sans Malayalam', 'Outfit', sans-serif;
  --radius: 1rem;
}

:root {
  --background: 40 30% 97%;
  --foreground: 160 50% 8%;

  --card: 0 0% 100%;
  --card-foreground: 160 50% 8%;

  --popover: 0 0% 100%;
  --popover-foreground: 160 50% 8%;

  --primary: 158 64% 28%;
  --primary-foreground: 0 0% 100%;

  --secondary: 40 40% 94%;
  --secondary-foreground: 40 50% 25%;

  --muted: 40 20% 94%;
  --muted-foreground: 160 15% 45%;

  --accent: 42 87% 55%;
  --accent-foreground: 42 90% 15%;

  --destructive: 0 84% 60%;
  --destructive-foreground: 0 0% 98%;

  --border: 40 20% 90%;
  --input: 40 20% 88%;
  --ring: 158 64% 28%;

  --chart-1: 158 64% 28%;
  --chart-2: 42 87% 55%;
  --chart-3: 160 40% 50%;
  --chart-4: 30 80% 60%;
  --chart-5: 200 60% 50%;
}

.dark {
  --background: 160 40% 4%;
  --foreground: 40 20% 96%;

  --card: 160 35% 7%;
  --card-foreground: 40 20% 96%;

  --popover: 160 35% 7%;
  --popover-foreground: 40 20% 96%;

  --primary: 158 55% 38%;
  --primary-foreground: 0 0% 100%;

  --secondary: 160 25% 12%;
  --secondary-foreground: 40 20% 96%;

  --muted: 160 25% 12%;
  --muted-foreground: 160 15% 65%;

  --accent: 42 87% 55%;
  --accent-foreground: 0 0% 100%;

  --destructive: 0 62% 30%;
  --destructive-foreground: 0 0% 98%;

  --border: 160 25% 12%;
  --input: 160 25% 15%;
  --ring: 158 55% 38%;
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer utilities {
  .glass {
    background: rgba(255, 255, 255, 0.85);
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
  }

  .glass-dark {
    background: rgba(0, 0, 0, 0.4);
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
  }

  .text-gradient {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--accent)) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .shadow-premium {
    box-shadow: 
      0 4px 6px -1px rgba(0, 0, 0, 0.05),
      0 10px 20px -5px rgba(0, 0, 0, 0.08),
      0 1px 3px rgba(0, 0, 0, 0.03);
  }

  .shadow-glow {
    box-shadow: 
      0 0 20px rgba(21, 128, 61, 0.15),
      0 4px 15px rgba(21, 128, 61, 0.1);
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  .animate-pulse-soft {
    animation: pulse-soft 2s ease-in-out infinite;
  }

  @keyframes pulse-soft {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.7; }
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(8px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .animate-fade-in {
    animation: fadeIn 0.6s ease-out forwards;
  }

  @keyframes iconBounce {
    0%, 100% {
      transform: translateY(0);
    }
    50% {
      transform: translateY(-6px);
    }
  }

  @keyframes iconSpin {
    0% {
      transform: rotate(0deg) scale(1);
    }
    50% {
      transform: rotate(10deg) scale(1.1);
    }
    100% {
      transform: rotate(0deg) scale(1);
    }
  }

  .animate-icon-bounce {
    animation: iconBounce 2s ease-in-out infinite;
  }

  .animate-icon-spin {
    animation: iconSpin 0.6s ease-in-out;
  }

  @keyframes pageFlip {
    0% {
      transform: rotateY(0deg);
    }
    50% {
      transform: rotateY(90deg);
    }
    100% {
      transform: rotateY(0deg);
    }
  }

  @keyframes handWave {
    0%, 100% {
      transform: rotate(0deg);
    }
    25% {
      transform: rotate(-15deg);
    }
    75% {
      transform: rotate(15deg);
    }
  }

  @keyframes smileExpression {
    0%, 100% {
      transform: scale(1) rotate(0deg);
    }
    50% {
      transform: scale(1.15) rotate(5deg);
    }
  }

  @keyframes spinSlow {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }

  @keyframes beadRoll {
    0%, 100% {
      transform: translateX(0) rotateZ(0deg);
    }
    25% {
      transform: translateX(3px) rotateZ(-10deg);
    }
    50% {
      transform: translateX(0) rotateZ(0deg);
    }
    75% {
      transform: translateX(-3px) rotateZ(10deg);
    }
  }

  @keyframes sparkleFlash {
    0%, 100% {
      opacity: 1;
      transform: scale(1);
    }
    50% {
      opacity: 0.6;
      transform: scale(1.2);
    }
  }

  .animate-page-flip {
    animation: pageFlip 3s ease-in-out infinite;
  }

  .animate-hand-wave {
    animation: handWave 2s ease-in-out infinite;
  }

  .animate-smile {
    animation: smileExpression 2.5s ease-in-out infinite;
  }

  .animate-spin-slow {
    animation: spinSlow 4s linear infinite;
  }

  .animate-bead-roll {
    animation: beadRoll 2s ease-in-out infinite;
  }

  .animate-sparkle {
    animation: sparkleFlash 1.5s ease-in-out infinite;
  }

  @keyframes logoRise {
    0% {
      opacity: 0;
      transform: translateY(40px);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes textFade {
    0% {
      opacity: 0;
    }
    100% {
      opacity: 1;
    }
  }

  @keyframes spinSlogoLogo {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }

  @keyframes shimmer {
    0% {
      transform: translateX(-100%);
    }
    100% {
      transform: translateX(100%);
    }
  }

  @keyframes bounceDot {
    0%, 100% {
      transform: translateY(0);
      opacity: 0.7;
    }
    50% {
      transform: translateY(-8px);
      opacity: 1;
    }
  }

  .animate-logo-rise {
    animation: logoRise 1s ease-out forwards;
  }

  .animate-text-fade {
    animation: textFade 1.2s ease-out 0.3s forwards;
    opacity: 0;
  }

  .animate-spin-slow-logo {
    animation: spinSlogoLogo 3s linear infinite;
  }

  .animate-shimmer {
    animation: shimmer 2s infinite;
  }

  .animate-bounce-dot {
    animation: bounceDot 1.4s infinite;
  }

  .pb-safe {
    padding-bottom: max(env(safe-area-inset-bottom), 0.5rem);
  }

  .font-arabic {
    font-family: 'Scheherazade New', 'Amiri Quran', 'Amiri', serif;
  }

  .font-bengali {
    font-family: 'Noto Sans Bengali', 'Outfit', sans-serif;
  }

  .font-urdu {
    font-family: 'Noto Nastaliq Urdu', 'Scheherazade New', serif;
  }

  .font-hindi {
    font-family: 'Noto Sans Devanagari', 'Outfit', sans-serif;
  }

  .font-tamil {
    font-family: 'Noto Sans Tamil', 'Outfit', sans-serif;
  }

  .font-malayalam {
    font-family: 'Noto Sans Malayalam', 'Outfit', sans-serif;
  }

  .font-translation {
    font-family: 'Lora', 'Outfit', serif;
  }

  @keyframes shimmerGlow {
    0% {
      transform: translateX(-100%);
      opacity: 0;
    }
    50% {
      opacity: 0.6;
    }
    100% {
      transform: translateX(100%);
      opacity: 0;
    }
  }

  .animate-shimmer-glow {
    animation: shimmerGlow 4s ease-in-out infinite;
  }

  @keyframes celebrationSlideIn {
    0% {
      opacity: 0;
      transform: translateY(-10px);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .animate-celebration {
    animation: celebrationSlideIn 0.6s ease-out forwards;
  }
}

